const GroupMembersDao = require('thankshell-libs/GroupMembersDao.js');


module.exports = class UserController {
  constructor(authId) {
    this.authId = authId;
  }
  async getAsync() {
    const groups = await (new GroupMembersDao()).findGroupsByAuthIdAsync(this.authId);

    return {
      groups: groups,
    }
  }
};
